#!/usr/bin/env python
# -*- coding:utf-8 -*-

import pytest


class TestExec:
    @pytest.mark.parametrize(
        'title, param, expected', [
            (
                "Normal",
                20,
                100
            ),
        ]
    )
    def test_normal(self, title, param, expected):
        actual = param * 5
        assert expected == actual
